    private void descargarFichero(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_descargarFichero
        JFileChooser dialogoFichero = new JFileChooser();
        if (dialogoFichero.showSaveDialog(this)==JFileChooser.APPROVE_OPTION) {
            File fichero = dialogoFichero.getSelectedFile();
            Socket conexion;
            try {
                conexion = new Socket(campoServidor.getText(),2222);
            } catch (UnknownHostException e) {
                JOptionPane.showMessageDialog(this, "No se ha podido encontrar el host", "Error", JOptionPane.ERROR_MESSAGE);
                return;
            } catch (IOException e) {
                JOptionPane.showMessageDialog(this, "No se ha podido establecer la conexi√≥n con el host", "Error", JOptionPane.ERROR_MESSAGE);
                return;
            }
            PrintWriter salida;
            InputStream entrada;
            FileOutputStream salidaFichero;
            try {
                salida = new PrintWriter(new OutputStreamWriter(conexion.getOutputStream()),true);
                entrada = conexion.getInputStream();
                salidaFichero = new FileOutputStream(fichero);
                int dato;
                System.out.println("Descargando " +campoFichero.getText());
                // Enviamos al servidor la ruta del fichero a descargar
                salida.println(campoFichero.getText());
                // Leemos el contenido binario enviado por el servidor
                while ((dato=entrada.read())!=-1)
                    salidaFichero.write(dato);
                salidaFichero.close();
                salida.close();
                entrada.close();
                conexion.close();
                JOptionPane.showMessageDialog(this, "Se ha completado la descarga", "Descarga completa", JOptionPane.INFORMATION_MESSAGE);
            } catch (IOException e) {
                JOptionPane.showMessageDialog(this, "Error de Entrada/Salida: " + e.getMessage(), "Error", JOptionPane.ERROR_MESSAGE);
            }
        }
    }//GEN-LAST:event_descargarFichero

